import React, { Component } from 'react';
import Button from './Button';



// const CREATE_EMPLOYEE_ROUTE = "/api/v1/admin/add-employees";
const PATH_TO_EMPLOYEE_LIST = '/api/v1/admin/all-employees';

class CreateEmployeeComponent extends Component {



    constructor(props, path){
        super(props)

        this.state = {

            firstName: "",
            lastNAme: "",
            emailAddress: ""

        }
        this.chnagefirstNameHandler = this.chnagefirstNameHandler.bind(this);
        this.chnageLastNameHandler = this.chnageLastNameHandler.bind(this);
        this.chnageEmailAddressHandler = this.chnageEmailAddressHandler.bind(this);
        this.saveEmployee = this.saveEmployee.bind(this)
    }


    saveEmployee = (e) =>{
        e.prevenentDefault();

        let employee = {firstName: this.state.firstName, lastNAme: this.state.lastNAme, emailAddress: this.state.emailAddress}
        console.log('employee => ' + JSON.stringify(employee));
    }


    chnagefirstNameHandler(event){
        this.setState({firstName: event.target.value});

    }


    chnageLastNameHandler(event){
        this.setState({lastNAme: event.target.value});
    }

    chnageEmailAddressHandler(event){
        this.setState({emailAddress: event.target.value});
    }

    cancel(){
        console.log('hello')
        window.location.href = "/";

    }

    render() {
        return (
            <div>
                <div className='container'>
                    <div className='row'>
                        <div className='card col-md-6 offset-md-3 offset-md-3 offset-md-3'>
                            <h3 className='text-center'>Add new Employee</h3>
                                <div className='card-body'>
                                    <form action="">
                                        <div className='form-group'>
                                            <label>First Name:</label>
                                            <input type="text" placeholder='First Name' className='form-control' 
                                            name='firstName' value={this.state.firstName} onChange={this.chnagefirstNameHandler} />
                                        </div>
                                        <div>
                                            <label>Last Name:</label>
                                            <input type="text" placeholder='Last Name' className='form-control' 
                                            name='lastName' value={this.state.lastName} onChange={this.chnageLastNameHandler} />
                                        </div>
                                        <div>
                                            <label>Email Address:</label>
                                            <input type="email" placeholder='Email address' className='form-control' 
                                            name='email' value={this.state.emailAddress} onChange={this.chnageEmailAddressHandler} />
                                        </div>
                                        <Button type="submit" classAttr="btn btn-success" title = "Save" onClick={this.saveEmployee} />
                                        <Button type="button" classAttr="btn btn-danger" title = "Cancel" onClick={() => this.cancel.bind(this)} />


                                    </form>
                                </div>
                        </div>

                    </div>


                </div>
            </div>
        );
    }
}

export default CreateEmployeeComponent;
